import React, { useState, useEffect, useCallback } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../context/AuthContext';
import { api } from '../services/supabase';
import LoadingSpinner from '../components/LoadingSpinner';
import mpesaLogo from '../img/mpesa.png';
import equityLogo from '../img/equity.png';
import kcbLogo from '../img/kcb.png';
import coopLogo from '../img/coop.png';
import absaLogo from '../img/absa.png';
import standardLogo from '../img/standard.png';
import './Dashboard.css';

// Kenyan Banks data
const KENYAN_BANKS = [
  { id: 'equity', name: 'Equity Bank', logo: equityLogo },
  { id: 'kcb', name: 'KCB Bank', logo: kcbLogo },
  { id: 'coop', name: 'Co-op Bank', logo: coopLogo },
  { id: 'absa', name: 'Absa Bank', logo: absaLogo },
  { id: 'standard', name: 'Standard Chartered', logo: standardLogo },
];

const Dashboard = () => {
  const { user, logout } = useAuth();
  const navigate = useNavigate();
  const [balance, setBalance] = useState(0);
  const [transactions, setTransactions] = useState([]);
  const [withdrawAmount, setWithdrawAmount] = useState('');
  const [depositAmount, setDepositAmount] = useState('');
  const [withdrawalMethod, setWithdrawalMethod] = useState('mpesa'); // 'mpesa' or 'bank'
  const [selectedBank, setSelectedBank] = useState(null);
  const [accountNumber, setAccountNumber] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('withdraw');
  const [showConfirmModal, setShowConfirmModal] = useState(false);
  const [showBankModal, setShowBankModal] = useState(false);
  const [confirmAmount, setConfirmAmount] = useState(0);
  const [confirmType, setConfirmType] = useState('');
  const [confirmMethod, setConfirmMethod] = useState('');

  const fetchData = useCallback(async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      navigate('/login');
      return;
    }

    try {
      // Fetch balance first
      const balanceData = await api.getBalance(token);
      if (balanceData.balance !== undefined) {
        setBalance(balanceData.balance);
      }

      // Then fetch transactions (sequential to avoid race conditions)
      const transactionsData = await api.getTransactions(token);
      if (transactionsData.transactions) {
        setTransactions(transactionsData.transactions);
      }
    } catch (err) {
      console.error('Error fetching data:', err);
      if (err.message && err.message.includes('401')) {
        navigate('/login');
      }
    }
  }, [navigate]);

  useEffect(() => {
    fetchData();
  }, [fetchData]);

  const handleBankSelect = (bank) => {
    setSelectedBank(bank);
    setAccountNumber('');
    setShowBankModal(false);
    setWithdrawalMethod('bank');
    setError('');
  };

  const handleChangeBank = () => {
    setSelectedBank(null);
    setAccountNumber('');
    setWithdrawalMethod('mpesa');
    setShowBankModal(true);
  };

  const initiateTransaction = (type, amount, method = '') => {
    setConfirmType(type);
    setConfirmAmount(parseFloat(amount));
    setConfirmMethod(method);
    setShowConfirmModal(true);
    setError('');
    setSuccess('');
  };

  const handleConfirmTransaction = async () => {
    setShowConfirmModal(false);
    const token = localStorage.getItem('token');

    if (confirmType === 'withdraw') {
      setLoading(true);
      try {
        const withdrawData = {
          amount: confirmAmount,
          withdrawal_method: confirmMethod.type || 'mpesa',
        };

        // Include bank details if bank withdrawal
        if (confirmMethod.type === 'bank' && confirmMethod.bank) {
          withdrawData.bank_id = confirmMethod.bank.id;
          withdrawData.bank_name = confirmMethod.bank.name;
          withdrawData.account_number = confirmMethod.accountNumber;
        }

        const result = await api.withdraw(token, confirmAmount, confirmMethod);
        if (result.error) {
          setError(result.error);
        } else {
          const methodDisplay = confirmMethod.type === 'bank' 
            ? `${confirmMethod.bank.name}` 
            : 'MPESA';
          setSuccess(`Successfully withdrew $${confirmAmount.toFixed(2)} via ${methodDisplay}`);
          setBalance(result.new_balance);
          fetchData();
          setWithdrawAmount('');
          // Reset bank selection after successful withdrawal
          setSelectedBank(null);
          setAccountNumber('');
        }
      } catch (err) {
        setError('Withdrawal failed. Please try again.');
      } finally {
        setLoading(false);
      }
    } else {
      setLoading(true);
      try {
        const result = await api.deposit(token, confirmAmount);
        if (result.error) {
          setError(result.error);
        } else {
          setSuccess(`Successfully deposited $${confirmAmount.toFixed(2)}`);
          setBalance(result.new_balance);
          fetchData();
          setDepositAmount('');
        }
      } catch (err) {
        setError('Deposit failed. Please try again.');
      } finally {
        setLoading(false);
      }
    }
  };

  const handleWithdraw = (e) => {
    e.preventDefault();
    const amount = parseFloat(withdrawAmount);

    if (!amount || amount <= 0) {
      setError('Please enter a valid amount');
      return;
    }

    if (amount < 10) {
      setError('Minimum withdrawal amount is $10');
      return;
    }

    if (amount > balance) {
      setError('Insufficient balance');
      return;
    }

    if (withdrawalMethod === 'bank' && !selectedBank) {
      setShowBankModal(true);
      return;
    }

    if (withdrawalMethod === 'bank' && !accountNumber) {
      setError('Please enter your account number');
      return;
    }

    const methodData = {
      type: withdrawalMethod,
      bank: selectedBank,
      accountNumber: accountNumber
    };

    initiateTransaction('withdraw', amount, methodData);
  };

  const handleDeposit = (e) => {
    e.preventDefault();
    const amount = parseFloat(depositAmount);

    if (!amount || amount <= 0) {
      setError('Please enter a valid amount');
      return;
    }

    if (amount < 1) {
      setError('Minimum deposit amount is $1');
      return;
    }

    if (amount > 50000) {
      setError('Maximum deposit amount is $50,000');
      return;
    }

    initiateTransaction('deposit', amount);
  };

  const handleLogout = async () => {
    await logout();
    localStorage.removeItem('token');
    navigate('/login');
  };

  const formatDate = (dateString) => {
    const date = new Date(dateString);
    return date.toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit'
    });
  };

  // Get display name for confirmation
  const getMethodDisplayName = (method) => {
    if (!method) return '';
    if (method.type === 'bank' && method.bank) {
      return method.bank.name;
    }
    return 'MPESA';
  };

  return (
    <div className="dashboard">
      {/* Bank Selection Modal */}
      {showBankModal && (
        <div className="modal-overlay" onClick={() => setShowBankModal(false)}>
          <div className="modal-content bank-modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>Select Your Bank</h3>
            <p>Choose from the list of Kenyan banks</p>
            <div className="bank-list">
              {KENYAN_BANKS.map((bank) => (
                <button
                  key={bank.id}
                  className="bank-option"
                  onClick={() => handleBankSelect(bank)}
                >
                  <img src={bank.logo} alt={bank.name} className="bank-logo" />
                  <span className="bank-name">{bank.name}</span>
                </button>
              ))}
            </div>
            <button 
              className="btn-cancel bank-modal-cancel"
              onClick={() => setShowBankModal(false)}
            >
              Cancel
            </button>
          </div>
        </div>
      )}

      {/* Confirmation Modal */}
      {showConfirmModal && (
        <div className="modal-overlay" onClick={() => setShowConfirmModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <h3>Confirm Transaction</h3>
            <p>
              Are you sure you want to {confirmType === 'withdraw' ? 'withdraw' : 'deposit'} 
              <strong> ${confirmAmount.toFixed(2)}</strong>
              {confirmType === 'withdraw' && confirmMethod && (
                <span> via <strong>{getMethodDisplayName(confirmMethod)}</strong></span>
              )}?
            </p>
            {confirmMethod && confirmMethod.type === 'bank' && confirmMethod.bank && (
              <div className="bank-details-preview">
                <p><strong>Bank:</strong> {confirmMethod.bank.name}</p>
                <p><strong>Account:</strong> {confirmMethod.accountNumber}</p>
              </div>
            )}
            <div className="modal-actions">
              <button 
                className="btn-cancel" 
                onClick={() => setShowConfirmModal(false)}
              >
                Cancel
              </button>
              <button 
                className={confirmType === 'withdraw' ? 'btn-confirm-withdraw' : 'btn-confirm-deposit'}
                onClick={handleConfirmTransaction}
              >
                Confirm
              </button>
            </div>
          </div>
        </div>
      )}

      <header className="dashboard-header">
        <div className="logo">
          <h1>üè¶ Online Bank</h1>
        </div>
        <div className="user-info">
          <Link to="/profile" className="btn-profile">üë§ Profile</Link>
          <span>Welcome, {user?.email}</span>
          <button onClick={handleLogout} className="btn-logout">
            Logout
          </button>
        </div>
      </header>

      <main className="dashboard-main">
        <div className="balance-card">
          <h2>Account Balance</h2>
          <div className="balance-amount">
            ${balance.toFixed(2)}
          </div>
        </div>

        <div className="transaction-section">
          <div className="transaction-tabs">
            <button 
              className={`tab ${activeTab === 'withdraw' ? 'active' : ''}`}
              onClick={() => setActiveTab('withdraw')}
            >
              üí∏ Withdraw
            </button>
            <button 
              className={`tab ${activeTab === 'deposit' ? 'active' : ''}`}
              onClick={() => setActiveTab('deposit')}
            >
              üí∞ Deposit
            </button>
          </div>

          <div className="transaction-form-container">
            {activeTab === 'withdraw' ? (
              <div className="withdraw-section">
                <h2>Withdraw Money</h2>
                <form onSubmit={handleWithdraw} className="withdraw-form">
                  {/* Withdrawal Method Selection */}
                  <div className="form-group">
                    <label>Withdraw To</label>
                    <div className="withdrawal-method-options">
                      <button
                        type="button"
                        className={`method-option ${withdrawalMethod === 'mpesa' ? 'active' : ''}`}
                        onClick={() => {
                          setWithdrawalMethod('mpesa');
                          setSelectedBank(null);
                          setAccountNumber('');
                        }}
                      >
                        <img src={mpesaLogo} alt="MPESA" className="method-icon method-logo" />
                        <span className="method-name">MPESA</span>
                      </button>
                      <button
                        type="button"
                        className={`method-option ${withdrawalMethod === 'bank' ? 'active' : ''}`}
                        onClick={() => setShowBankModal(true)}
                      >
                        <span className="method-icon">üè¶</span>
                        <span className="method-name">BANK</span>
                      </button>
                    </div>
                  </div>

                  {/* Selected Bank Display */}
                  {withdrawalMethod === 'bank' && selectedBank && (
                    <div className="selected-bank-display">
                      <img src={selectedBank.logo} alt={selectedBank.name} className="selected-bank-logo" />
                      <div className="selected-bank-info">
                        <span className="selected-bank-name">{selectedBank.name}</span>
                        <button 
                          type="button" 
                          className="change-bank-btn"
                          onClick={handleChangeBank}
                        >
                          Change Bank
                        </button>
                      </div>
                    </div>
                  )}

                  {/* Account Number Input (for bank withdrawal) */}
                  {withdrawalMethod === 'bank' && selectedBank && (
                    <div className="form-group">
                      <label htmlFor="accountNumber">Account Number</label>
                      <input
                        type="text"
                        id="accountNumber"
                        value={accountNumber}
                        onChange={(e) => setAccountNumber(e.target.value)}
                        placeholder="Enter account number"
                        maxLength="20"
                      />
                    </div>
                  )}

                  <div className="form-group">
                    <label htmlFor="amount">Amount ($)</label>
                    <input
                      type="number"
                      id="amount"
                      value={withdrawAmount}
                      onChange={(e) => setWithdrawAmount(e.target.value)}
                      placeholder="Enter amount (min $10)"
                      min="10"
                      step="0.01"
                    />
                  </div>
                  <button type="submit" className="btn-withdraw" disabled={loading}>
                    {loading ? <LoadingSpinner size="small" message="" /> : 'Proceed to Withdraw'}
                  </button>
                </form>
              </div>
            ) : (
              <div className="deposit-section">
                <h2>Deposit Money</h2>
                <form onSubmit={handleDeposit} className="deposit-form">
                  <div className="form-group">
                    <label htmlFor="depositAmount">Amount ($)</label>
                    <input
                      type="number"
                      id="depositAmount"
                      value={depositAmount}
                      onChange={(e) => setDepositAmount(e.target.value)}
                      placeholder="Enter amount (min $1, max $50,000)"
                      min="1"
                      max="50000"
                      step="0.01"
                    />
                  </div>
                  <button type="submit" className="btn-deposit" disabled={loading}>
                    {loading ? <LoadingSpinner size="small" message="" /> : 'Proceed to Deposit'}
                  </button>
                </form>
              </div>
            )}

            {error && <div className="error-message">{error}</div>}
            {success && <div className="success-message">{success}</div>}
          </div>
        </div>

        <div className="transactions-section">
          <h2>Transaction History</h2>
          {transactions.length === 0 ? (
            <p className="no-transactions">No transactions yet</p>
          ) : (
            <div className="transactions-list">
              {transactions.map((tx) => (
                <div key={tx.id} className={`transaction-item ${tx.type}`}>
                  <div className="transaction-info">
                    <span className="transaction-type">
                      {tx.type === 'deposit' ? 'üí∞' : 'üí∏'} {tx.type.charAt(0).toUpperCase() + tx.type.slice(1)}
                    </span>
                    <span className="transaction-description">
                      {tx.description}
                    </span>
                  </div>
                  <div className="transaction-details">
                    <span className={`transaction-amount ${tx.type}`}>
                      {tx.type === 'withdrawal' ? '-' : '+'}${Math.abs(tx.amount).toFixed(2)}
                    </span>
                    <span className="transaction-date">
                      {formatDate(tx.created_at)}
                    </span>
                  </div>
                </div>
              ))}
            </div>
          )}
        </div>
      </main>
    </div>
  );
};

export default Dashboard;
